import { useState } from "react";
import { React } from "react";
import SquareX from "./components/02-tic-tac-toe/SquareX";

// import Board from "./components/tic-tac-toe/Tic";
// import Game from "./components/tic-tac-toe/Tac";
// import PicBg from "./components/pic-bg/PicBg";
// import EditProfile from "./components/edit-profile/EditProfile";
// import MailClient from "./components/letter-highlight/MailClient";
// import MailSelect from "./components/letter-select/MailSelect";
// import TaskApp from "./components/task-app/TaskApp";
// import HeadingApp from "./components/heading-context/HeadingApp";
// import PostApp from "./components/heading-post/PostApp";

// 02-tic-tac-toe
// Lift state up again to add time travel
export default function Game() {
	const [xIsNext, setXIsNext] = useState(true);
	const [history, setHistory] = useState([Array(9).fill(null)]);
	// Read the last squares array from the history
	const currentSquares = history[history.length - 1];
	// To keep track of which step the user is currently viewing
	const [currentMove, setCurrentMove] = useState(0);

	// Function to update the game
	function handlePlay(nextSquares) {
		setHistory([...history, nextSquares]);
		setXIsNext(!xIsNext);
	}

	function jumpTo(nextMove) {
		// set xIsNext to true if the number that you’re changing currentMove to is even
		setCurrentMove(nextMove);
		setXIsNext(nextMove % 2 === 0);
	}

	const moves = history.map((squares, move) => {
		let description;
		if (move > 0) {
			description = "Go to move #" + move;
		} else {
			description = "Go to game start";
		}
		return (
			// it’s safe to use the move index as a key
			<li key={move}>
				<button onClick={() => jumpTo(move)}>{description}</button>
			</li>
		);
	});

	return (
		<div className="game">
			<div className="game-board">
				<Board xIsNext={xIsNext} squares={currentSquares} onPlay={handlePlay} />
			</div>
			<div className="game-info">
				<ol>{moves}</ol>
			</div>
		</div>
	);
}

function Board({ xIsNext, squares, onPlay }) {
	// Lift the state up to Game component; See `setHistory`, `setXIsNext`
	// const [xIsNext, setXIsNext] = useState(true);
	// const [squares, setSquares] = useState(Array(9).fill(null));

	// Set X on each square
	/* function handleClick(i) {
		const nextSquares = squares.slice();
		nextSquares[i] = "X";
		setSquares(nextSquares);
	} */

	// Set O after setting X
	function handleClick(i) {
		// To prevent the X being overwritten by an O when clicking on the same square multiple times!
		if (squares[i] || calculateWinner(squares)) {
			return;
		}

		const nextSquares = squares.slice();
		if (xIsNext) {
			nextSquares[i] = "X";
		} else {
			nextSquares[i] = "O";
		}
		// Replace the following code with onPlay
		// setSquares(nextSquares);
		// setXIsNext(!xIsNext);
		onPlay(nextSquares);
	}

	// Add status to display results of the game
	const winner = calculateWinner(squares);
	let status;
	if (winner) {
		status = "Winner: " + winner;
	} else {
		status = "Next player: " + (xIsNext ? "X" : "O");
	}

	return (
		<>
			<div className="status">{status}</div>
			<div className="board-row">
				<SquareX value={squares[0]} onSquareClick={() => handleClick(0)} />
				<SquareX value={squares[1]} onSquareClick={() => handleClick(1)} />
				<SquareX value={squares[2]} onSquareClick={() => handleClick(2)} />
			</div>
			<div className="board-row">
				<SquareX value={squares[3]} onSquareClick={() => handleClick(3)} />
				<SquareX value={squares[4]} onSquareClick={() => handleClick(4)} />
				<SquareX value={squares[5]} onSquareClick={() => handleClick(5)} />
			</div>
			<div className="board-row">
				<SquareX value={squares[6]} onSquareClick={() => handleClick(6)} />
				<SquareX value={squares[7]} onSquareClick={() => handleClick(7)} />
				<SquareX value={squares[8]} onSquareClick={() => handleClick(8)} />
			</div>
		</>
	);
}
// To declare a winner in tic-tac-toe
function calculateWinner(squares) {
	const lines = [
		[0, 1, 2],
		[3, 4, 5],
		[6, 7, 8],
		[0, 3, 6],
		[1, 4, 7],
		[2, 5, 8],
		[0, 4, 8],
		[2, 4, 6],
	];
	for (let i = 0; i < lines.length; i++) {
		const [a, b, c] = lines[i];
		if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
			return squares[a];
		}
	}
	return null;
}
