// 02-tic-tac-toe
export default function Board() {
	const [xIsNext, setXIsNext] = useState(true);
	const [squares, setSquares] = useState(Array(9).fill(null));

	// Set X on each square
	/* function handleClick(i) {
		const nextSquares = squares.slice();
		nextSquares[i] = "X";
		setSquares(nextSquares);
	} */

	// Set O after setting X
	function handleClick(i) {
		// To prevent the X being overwritten by an O when clicking on the same square multiple times!
		if (squares[i] || calculateWinner(squares)) {
			return;
		}
		const nextSquares = squares.slice();
		if (xIsNext) {
			nextSquares[i] = "X";
		} else {
			nextSquares[i] = "O";
		}
		setSquares(nextSquares);
		setXIsNext(!xIsNext);
	}

	return (
		<>
			<div className="board-row">
				<SquareX value={squares[0]} onSquareClick={() => handleClick(0)} />
				<SquareX value={squares[1]} onSquareClick={() => handleClick(1)} />
				<SquareX value={squares[2]} onSquareClick={() => handleClick(2)} />
			</div>
			<div className="board-row">
				<SquareX value={squares[3]} onSquareClick={() => handleClick(3)} />
				<SquareX value={squares[4]} onSquareClick={() => handleClick(4)} />
				<SquareX value={squares[5]} onSquareClick={() => handleClick(5)} />
			</div>
			<div className="board-row">
				<SquareX value={squares[6]} onSquareClick={() => handleClick(6)} />
				<SquareX value={squares[7]} onSquareClick={() => handleClick(7)} />
				<SquareX value={squares[8]} onSquareClick={() => handleClick(8)} />
			</div>
		</>
	);
}
// To declare a winner in tic-tac-toe
function calculateWinner(squares) {
	const lines = [
		[0, 1, 2],
		[3, 4, 5],
		[6, 7, 8],
		[0, 3, 6],
		[1, 4, 7],
		[2, 5, 8],
		[0, 4, 8],
		[2, 4, 6],
	];
	for (let i = 0; i < lines.length; i++) {
		const [a, b, c] = lines[i];
		if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
			return squares[a];
		}
	}
	return null;
}

