const user = {
	name: "Hedy Lamarr",
	imageUrl: "https://i.imgur.com/yXOvdOSs.jpg",
	imageSize: 90,
	isLoggedIn: true,
};
function Image() {
	return (
		<>
			<h1>{user.name}</h1>
			<img
				className="avatar"
				src={user.imageUrl}
				alt={"Photo of " + user.name}
				style={{
					width: user.imageSize,
					height: user.imageSize,
				}}
			/>
		</>
	);
}
function Default() {
	return (
		<>
			<p>
				Hello there.
				<br />
				How do you do?
			</p>
			<button>I'm a button</button>
		</>
	);
}

